using Bunifu.UI.WinForms.BunifuButton;using System;using System.Collections.Generic;using System.ComponentModel;using System.Data;using System.Drawing;using System.Linq;using System.Messaging;using System.Runtime.CompilerServices;using System.Text;using System.Threading.Tasks;using System.Windows.Forms;using System.Xml.Linq;namespace HP3{	public partial class frmMain : Form	{		public frmMain()		{			InitializeComponent();		}		private string bttName;		private int yPos = 0;        private string altBttName;		public void moverIndicador(int Y)		{			indicator.Location = new Point(indicator.Location.X, Y);			indicator.BringToFront();		}        public string actualizarNombre(string bttName)		{            string modName;            if (bttName.StartsWith("mainBtt"))            {                string aRemover = "mainBtt";                modName = bttName.Replace(aRemover, "");                return modName;            }            else if (bttName.StartsWith("tablasBtt"))            {                string aRemover = "tablasBtt";                modName = bttName.Replace(aRemover, "");                return modName;            }            else            {                MessageBox.Show("No se pudo realizar el cambio en la variable");                modName = bttName;                return modName;            }        }        public void moverPageTabs(string actName)        {            //  Cambia las tabPages del mainPages            mainPages.SetPage(actName);            //  Cambia las tabPages del tablasPages            tablasPages.SetPage(actName);                    }        private void mainClickBotones(object sender, EventArgs e)        {            // Opciones para obtener campos del boton            BunifuButton botonClickeado = (BunifuButton)sender;            // Obtiene el Nombre del Boton            bttName = botonClickeado.Name;            // Obtiene la posicion "Y" del boton            yPos = botonClickeado.Location.Y;            // Nuevo nombre Retornado (con la modificacion incluida)            string newName = actualizarNombre(bttName);            // Ejecuta los metodos para el pageMain            moverPageTabs(newName); // Utiliza newName en lugar de bttName            moverIndicador(yPos);        }        //  Boton cerrar sesion - Salir        private void mainBttExit_Click(object sender, EventArgs e)        {            //  Hay que mejorar el metodo para que si cierre el programa            this.Close();            frmLogin login = new frmLogin();            login.Show();        }        private void altClickBotones(object sender, EventArgs e)        {            BunifuButton botonClickeado = (BunifuButton)sender;            altBttName = botonClickeado.Name;            string newName = actualizarNombre(altBttName);                        moverPageTabs(newName);        }            }}